mapper.select:
        select  t.config_id,t.config_id configId,t.irc_id,t.irc_id ircId,t.status_cd,t.status_cd statusCd,t.rule_id,t.rule_id ruleId,t.community_id,t.community_id communityId 
from integral_rule_config t 
where 1 =1 

   and t.config_id= #{configId}


   and t.irc_id= #{ircId}


   and t.status_cd= #{statusCd}


   and t.rule_id= #{ruleId}


   and t.community_id= #{communityId}

order by t.create_time desc

   limit #{page}, #{row}

mapper.update:
        update  integral_rule_config t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.config_id= #{configId}


, t.rule_id= #{ruleId}


, t.community_id= #{communityId}

 where 1=1 
and t.irc_id= #{ircId}

mapper.select:
        select  count(1) count 
from integral_rule_config t 
where 1 =1 

   and t.config_id= #{configId}


   and t.irc_id= #{ircId}


   and t.status_cd= #{statusCd}


   and t.rule_id= #{ruleId}


   and t.community_id= #{communityId}
