mapper.select:
select t.dps_id,t.dps_id dpsId,t.dp_id,t.dp_id dpId,t.staff_name,t.staff_name
staffName,t.remark,t.status_cd,t.status_cd statusCd,t.community_id,t.community_id
communityId,t.staff_id,t.staff_id staffId
from data_privilege_staff t
where 1 =1

    and t.dps_id= #{dpsId}


    and t.dp_id= #{dpId}


    and t.staff_name= #{staffName}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.staff_id= #{staffId}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update data_privilege_staff t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}



    , t.dp_id= #{dpId}


    , t.staff_name= #{staffName}


    , t.remark= #{remark}



    , t.staff_id= #{staffId}

where 1=1

    and t.community_id= #{communityId}


    and t.dps_id= #{dpsId}

mapper.select:
select count(1) count
from data_privilege_staff t
where 1 =1

    and t.dps_id= #{dpsId}


    and t.dp_id= #{dpId}


    and t.staff_name= #{staffName}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.community_id= #{communityId}


    and t.staff_id= #{staffId}
