mapper.select:
select t.amount,t.obj_id,t.obj_id objId,t.remark,t.status_cd,t.status_cd statusCd,t.obj_name,t.obj_name
objName,t.community_id,t.community_id communityId,t.receipt_id,t.receipt_id receiptId,t.obj_type,t.obj_type
objType
from fee_receipt t
where 1 =1

    and t.amount= #{amount}


    and t.obj_id= #{objId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.obj_name= #{objName}


    and t.community_id= #{communityId}


    and t.receipt_id= #{receiptId}


    and t.obj_type= #{objType}

order by t.create_time desc

    limit #{page}, #{row}

mapper.update:
update fee_receipt t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.amount= #{amount}


    , t.obj_id= #{objId}


    , t.remark= #{remark}


    , t.obj_name= #{objName}


    , t.community_id= #{communityId}


    , t.obj_type= #{objType}

where 1=1

    and t.receipt_id= #{receiptId}

mapper.select:
select count(1) count
from fee_receipt t
where 1 =1

    and t.amount= #{amount}


    and t.obj_id= #{objId}


    and t.remark= #{remark}


    and t.status_cd= #{statusCd}


    and t.obj_name= #{objName}


    and t.community_id= #{communityId}


    and t.receipt_id= #{receiptId}


    and t.obj_type= #{objType}
