mapper.select:
           select  t.operate,t.sex,t.name,t.link,t.remark,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,t.user_id,t.user_id userId,t.age,t.member_id,t.member_id memberId 
from business_building_owner t 
where 1 =1 

   and t.operate= #{operate}


   and t.sex= #{sex}


   and t.name= #{name}


   and t.link= #{link}


   and t.remark= #{remark}


   and t.owner_id= #{ownerId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}


   and t.age= #{age}


   and t.member_id= #{memberId}

mapper.insert:
        insert into building_owner(
sex,name,link,status_cd,remark,owner_id,b_id,user_id,age,member_id
) select t.sex,t.name,t.link,'0',t.remark,t.owner_id,t.b_id,t.user_id,t.age,t.member_id from business_building_owner t where 1=1
   and t.operate= 'ADD'

   and t.sex= #{sex}


   and t.name= #{name}


   and t.link= #{link}


   and t.remark= #{remark}


   and t.owner_id= #{ownerId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}


   and t.age= #{age}


   and t.member_id= #{memberId}

mapper.select:
        select  t.sex,t.name,t.link,t.status_cd,t.status_cd statusCd,t.remark,t.owner_id,t.owner_id ownerId,t.b_id,t.b_id bId,t.user_id,t.user_id userId,t.age,t.member_id,t.member_id memberId 
from building_owner t 
where 1 =1 

   and t.sex= #{sex}


   and t.name= #{name}


   and t.link= #{link}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.owner_id= #{ownerId}


   and t.b_id= #{bId}


   and t.user_id= #{userId}


   and t.age= #{age}


   and t.member_id= #{memberId}


   limit #{page}, #{row}

mapper.update:
        update  building_owner t set t.status_cd = #{statusCd}

,t.b_id = #{newBId}


, t.sex= #{sex}


, t.name= #{name}


, t.link= #{link}


, t.remark= #{remark}


, t.owner_id= #{ownerId}


, t.user_id= #{userId}


, t.age= #{age}

 where 1=1 
and t.b_id= #{bId}


and t.member_id= #{memberId}

mapper.select:
        select  count(1) count 
from building_owner t 
where 1 =1 

   and t.sex= #{sex}


   and t.name= #{name}


   and t.link= #{link}


   and t.status_cd= #{statusCd}


   and t.remark= #{remark}


   and t.owner_id= #{ownerId}


