mapper.select:
select t.v_id,t.v_id vId,t.departure_time,t.departure_time departureTime,t.visit_time,t.visit_time
visitTime,t.phoneNumber,t.operate,t.name,t.visit_case,t.visit_case visitCase,t.visit_gender,t.visit_gender
visitGender,t.owner_id,t.owner_id ownerId,t.userId
from business_visit_info t
where 1 =1

    and t.v_id= #{vId}


    and t.departure_time= #{departureTime}


    and t.visit_time= #{visitTime}


    and t.phoneNumber= #{phoneNumber}


    and t.operate= #{operate}


    and t.name= #{name}


    and t.visit_case= #{visitCase}


    and t.visit_gender= #{visitGender}


    and t.owner_id= #{ownerId}


    and t.userId= #{userId}

mapper.insert:
insert into visit_info(
v_id,departure_time,visit_time,phoneNumber,name,visit_case,status_cd,visit_gender,owner_id,userId
) select
t.v_id,t.departure_time,t.visit_time,t.phoneNumber,t.name,t.visit_case,'0',t.visit_gender,t.owner_id,t.userId
from business_visit_info t where 1=1

    and t.v_id= #{vId}


    and t.departure_time= #{departureTime}


    and t.visit_time= #{visitTime}


    and t.phoneNumber= #{phoneNumber}

and t.operate= 'ADD'

    and t.name= #{name}


    and t.visit_case= #{visitCase}


    and t.visit_gender= #{visitGender}


    and t.owner_id= #{ownerId}


    and t.userId= #{userId}

mapper.select:
select t.v_id,t.v_id vId,t.departure_time,t.departure_time departureTime,t.visit_time,t.visit_time
visitTime,t.phoneNumber,t.name,t.visit_case,t.visit_case visitCase,t.status_cd,t.status_cd
statusCd,t.visit_gender,t.visit_gender visitGender,t.owner_id,t.owner_id ownerId,t.userId
from visit_info t
where 1 =1

    and t.v_id= #{vId}


    and t.departure_time= #{departureTime}


    and t.visit_time= #{visitTime}


    and t.phoneNumber= #{phoneNumber}


    and t.name= #{name}


    and t.visit_case= #{visitCase}


    and t.status_cd= #{statusCd}


    and t.visit_gender= #{visitGender}


    and t.owner_id= #{ownerId}


    and t.userId= #{userId}


    limit #{page}, #{row}

mapper.update:
update visit_info t set t.status_cd = #{statusCd}

    ,t.b_id = #{newBId}


    , t.departure_time= #{departureTime}


    , t.visit_time= #{visitTime}


    , t.phoneNumber= #{phoneNumber}


    , t.name= #{name}


    , t.visit_case= #{visitCase}


    , t.visit_gender= #{visitGender}


    , t.owner_id= #{ownerId}


    , t.userId= #{userId}

where 1=1

    and t.v_id= #{vId}

mapper.select:
select count(1) count
from visit_info t
where 1 =1

    and t.v_id= #{vId}


    and t.departure_time= #{departureTime}


    and t.visit_time= #{visitTime}


    and t.phoneNumber= #{phoneNumber}


    and t.name= #{name}


    and t.visit_case= #{visitCase}


    and t.status_cd= #{statusCd}


    and t.visit_gender= #{visitGender}


    and t.owner_id= #{ownerId}


    and t.userId= #{userId}
